import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { first, Observable } from "rxjs";
import { environment } from "../../../environments/environment";

@Injectable({
  providedIn: "root",
})
export class ApiService {
  constructor(private http: HttpClient) {}

  // eslint-disable-next-line no-undef
  get(path: string, params?: any, options?: any): Observable<any> {
    return this.http.get(environment.apiUrl + path, { params, ...options }).pipe(first());
  }

  put(path: string, body: object): Observable<any> {
    return this.http.put(environment.apiUrl + path, body).pipe(first());
  }

  post(path: string, body: object): Observable<any> {
    return this.http.post(environment.apiUrl + path, body).pipe(first());
  }

  delete(path: string): Observable<any> {
    return this.http.delete(environment.apiUrl + path).pipe(first());
  }
}

import { Injectable } from "@angular/core";
import { ApiService } from "../../core/services";
import { map, Observable } from "rxjs";
import {
  LoginRequest,
  LoginResponse,
  RefreshResponse,
  RegisterRequest,
  RegisterResponse,
} from "../models/http.model";
import { plainToClass } from "class-transformer";
import { Tokens } from "../models/tokens";

@Injectable()
export class AuthService {
  constructor(private apiService: ApiService) {}

  login({ email, password }: LoginRequest): Observable<LoginResponse> {
    return this.apiService
      .post("/auth/login", { email, password })
      .pipe(map(json => plainToClass(LoginResponse, json)));
  }

  register(data: RegisterRequest): Observable<RegisterResponse> {
    return this.apiService
      .post("/auth/register", data)
      .pipe(map(json => plainToClass(RegisterResponse, json)));
  }

  refreshTokens(): Observable<RefreshResponse> {
    return this.apiService
      .post("/auth/refresh-tokens", { refresh_token: localStorage.getItem("refreshToken") })
      .pipe(map(json => plainToClass(RefreshResponse, json)));
  }

  getTokens(): Tokens | null {
    const accessToken =
      localStorage.getItem("accessToken") ?? sessionStorage.getItem("accessToken");
    const refreshToken =
      localStorage.getItem("refreshToken") ?? sessionStorage.getItem("refreshToken");

    if (!accessToken && !refreshToken) {
      return null;
    }

    return { accessToken: accessToken as string, refreshToken: accessToken as string };
  }

  memTokens(tokens: Tokens, session = false): void {
    if (!session) {
      localStorage.setItem("accessToken", tokens.accessToken);
      localStorage.setItem("refreshToken", tokens.refreshToken);
    } else {
      sessionStorage.setItem("accessToken", tokens.accessToken);
      sessionStorage.setItem("refreshToken", tokens.refreshToken);
    }
  }
}

import { Injectable } from "@angular/core";
import { Router } from "@angular/router";
import { ErrorCode } from "../models/error-code";

@Injectable({
  providedIn: "root",
})
export class ErrorService {
  constructor(private router: Router) {}

  throwNotFount(): Promise<void> {
    return this.throwError(ErrorCode.NOT_FOUND);
  }

  throwServerError(): Promise<void> {
    return this.throwError(ErrorCode.SERVER_ERROR);
  }

  private throwError(type: ErrorCode): Promise<void> {
    return this.router.navigateByUrl(`/error/${type}`).then(() => console.debug("Route Changed"));
  }
}

import { Injectable } from "@angular/core";
import { ValidatorFn } from "@angular/forms";

@Injectable({
  providedIn: "root",
})
export class ValidationService {
  constructor() {}

  useMatchValidator(left: string, right: string): ValidatorFn {
    return group => {
      if (!group.get(left) || !group.get(right)) {
        throw new Error(`No control with name ${left} or ${right}`);
      }

      // @ts-ignore
      return group.get(left).value !== group.get(right).value ? { unMatch: true } : null;
    };
  }
}

