import { Injectable } from "@angular/core";
import {
  HttpErrorResponse,
  HttpEvent,
  HttpHandler,
  HttpHeaders,
  HttpInterceptor,
  HttpRequest,
} from "@angular/common/http";
import { catchError, Observable, switchMap, throwError } from "rxjs";
import { AuthService } from "../../auth/services";

@Injectable()
export class BearerTokenInterceptor implements HttpInterceptor {
  constructor(private authService: AuthService) {}

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    const headers = new HttpHeaders();
    const tokens = this.authService.getTokens();

    if (tokens) {
      headers.set("Authorization", `Bearer ${tokens.accessToken}`);
    }

    const req = request.clone({ headers });
    return next.handle(req).pipe(
      catchError((error: HttpErrorResponse) => {
        if (error.status === 401) {
          return this.authService.refreshTokens().pipe(
            switchMap(res => {
              this.authService.memTokens(res);
              const headers = new HttpHeaders();

              const tokens = this.authService.getTokens();

              if (tokens) {
                headers.set("Authorization", `Bearer ${tokens.accessToken}`);
              }

              return next.handle(
                request.clone({
                  headers,
                })
              );
            })
          );
        }

        return throwError(() => error);
      })
    );
  }
}

import { Injectable } from "@angular/core";
import {
  HttpEvent,
  HttpHandler,
  HttpInterceptor,
  HttpRequest,
  HttpResponse,
} from "@angular/common/http";
import { map, Observable } from "rxjs";
import * as snakecaseKeys from "snakecase-keys";
import camelcaseKeys from "camelcase-keys";

@Injectable()
export class CamelcaseInterceptor implements HttpInterceptor {
  constructor() {}

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    const req = request.clone({
      // @ts-ignore
      body: snakecaseKeys(request.body, { deep: true }),
    });

    return next.handle(req).pipe(
      map((event: HttpEvent<any>) => {
        if (event instanceof HttpResponse) {
          return event.clone({
            body: camelcaseKeys(event.body, { deep: true }),
          });
        }

        return event;
      })
    );
  }
}

